muclient.script:
require "world_miniwindow"

-----------------
-- plugin stuff
-----------------

function OnPluginInstall()
  Tell("-- " .. GetPluginInfo(GetPluginID(), 1) .. ": type ")
  ColourTell("silver", "black", GetPluginInfo(GetPluginID(), 1) .. " help")
  Note(" to see info about this plugin --")

  do_Install()
end


function OnPluginSaveState()
  do_SaveState()
end


function OnPluginClose()
  do_Close()
end


function OnPluginDisable()
  do_Disable()
end


function OnPluginEnable()
  do_SaveState()
  do_Install()
end


function OnHelp()
  ColourNote("silver", "black", world.GetPluginInfo(world.GetPluginID(), 3))
  Note("")
  ColourNote("silver", "black", "(this version: " .. os.date("%c", GetPluginInfo(GetPluginID(), 14)) .. ")")
end


function plugin_update_url()
  local t = {
    "https://raw.githubusercontent.com/MateriaMagicaLLC/mm-mushclient-scripts/master/src/world_mini.xml",
  }
  return (table.concat(t, ";"))
end


function plugin_update_aux_url()
  local t = {
    "https://raw.githubusercontent.com/MateriaMagicaLLC/mm-mushclient-scripts/master/src/world_miniwindow.lua,MUSH/lua",
  }
  return (table.concat(t, ";"))
end



----------------
-- other stuff
----------------

function reset_main()
  do_reset_mini()
end
